import React from 'react'

class Somar extends React.Component {

  state = {
    numero1 : null,
    numero2 : null,
    resultado : null
  }

  somar = () => {
    const resultado = parseInt(this.state.numero1) + parseInt(this.state.numero2)
    this.setState({resultado: resultado})
  }

}

export default Somar;
